SELECT te.base_table_name as 'baseTableName', ap.project_name as 'projectName', ap.require_attr as 'requireAttr', ap.require_time_type as 'requireTimeType', ap.data_expir as 'dataExpir', ap.applicant_name as 'applicantName', ap.applicant_department as 'applicantDepartment', ap.create_time as 'applyTime', ap.id as applicationId, ap.current_audit_process_status as 'currentAuditProcessStatus' FROM tg_application_info ap LEFT JOIN tg_template_info te ON ap.template_id = te.id LEFT JOIN tg_audit_process_info au ON ap.process_id = au.process_id
FIND_IN_SET(#{userId},ap.current_handlers) and ap.copy = 0
and ap.require_time_type = #{search.searchRequireTimeType}
and ap.client_names like concat('%',#{search.searchClient},'%')
and ap.require_attr = #{search.searchReqiureAttr}
and au.current_audit_status = #{search.searchProcessStatus}
and ap.project_name like concat('%',#{search.searchProjectName},'%')
and te.base_table_name like concat('%',#{search.searchTableName},'%')
order by ap.update_time desc

SELECT ap.*
FROM tg_application_info ap
LEFT JOIN tg_template_info te ON ap.template_id = te.id
LEFT JOIN tg_audit_process_info au ON ap.process_id = au.process_id

    ap.applicant_id = #{userId}
    and ap.copy = 0

        and ap.require_time_type = #{search.searchRequireTimeType}


        and ap.client_names like concat('%',#{search.searchClient},'%')


        and ap.require_attr = #{search.searchReqiureAttr}


        and ap.current_audit_process_status = #{search.searchProcessStatus}


        and (ap.project_name like concat('%',#{search.searchContent},'%')
        or te.base_table_name like concat('%',#{search.searchContent},'%'))


order by ap.update_time desc

SELECT * FROM ( SELECT ap.base_table_name as 'baseTableName', ap.project_name as 'projectName', ap.require_attr as 'requireAttr', ap.client_names as 'readableUsers', ap.require_time_type as 'requireTimeType',ap.data_expir as 'dataExpir', ap.create_time as 'applyTime', ap.apply_type as 'applyType', ap.update_time, ap.id as applicationId, ap.applicant_id as applicantId, ap.id as id, ap.current_audit_process_status as 'currentAuditProcessStatus', ap.current_handlers as 'currentHadnlers', ap.current_index as 'currentIndex', #{search.applicationType} as applicationType, te.template_name as 'templateName', ap.base_table_id as 'baseTableId',ap.apply_reason as 'applyReason', tp.name as 'newProjectName' FROM tg_application_info ap LEFT JOIN tg_template_info te ON ap.template_id = te.id
LEFT JOIN table_info ti ON ti.id = ap.base_table_id
LEFT JOIN tg_project tp ON tp.id = ap.project_id LEFT JOIN tg_audit_process_info au ON te.process_id = au.process_id
ap.applicant_id = #{userId} and ap.copy = 0 and ap.application_type = #{search.applicationType}
and ap.require_time_type = #{search.searchRequireTimeType}
and ap.require_attr = #{search.searchReqiureAttr}
and ap.current_audit_process_status = #{search.searchProcessStatus}
and ( ap.base_table_name like concat('%',#{search.searchContent},'%') or ap.project_name like concat('%',#{search.searchContent},'%') or ap.applicant_name like concat('%',#{search.searchContent},'%')
or ti.table_alias like concat('%',#{search.searchContent},'%')
)
and ap.client_names like concat('%',#{search.searchClient},'%')
ORDER BY au.process_version DESC LIMIT 100000 ) a GROUP BY id ORDER BY update_time desc

SELECT a.*, b.base_table_id, (SELECT GROUP_CONCAT( DISTINCT template_name SEPARATOR '„ÄÅ') from tg_template_info where process_id = a.process_id) template_name FROM (SELECT * FROM (SELECT * FROM tg_audit_process_info ORDER BY process_version desc limit 100000) t GROUP BY t.process_id ORDER BY id asc) a LEFT JOIN tg_template_info b ON a.process_id = b.process_id
id = #{params.id}
and template_name = #{params.searchTemplateName}
and process_name like concat('%',#{params.searchProcessName},'%')
GROUP BY process_id, process_version order by a.update_time desc

select *
from tg_audit_process_info
where process_id = #{processId}
  and process_version = #{processVersion}

SELECT ap.id, ap.template_id, ap.template_version, ap.applicant_id, ap.applicant_name, ap.applicant_department, ap.project_name, ap.require_attr, ap.require_time_type, ap.data_expir, ap.handle_node_json, ap.table_alias_mapping_json, ap.handle_index_mapping_json, ap.status, ap.current_audit_status, ap.creator, ap.updater, ap.create_time, ap.update_time, ap.apply_passed_time, ap.process_id, ap.current_index, ap.current_handlers, ap.status_chain, ap.process_version, ap.all_handlers, ap.base_table_id, ap.base_table_name, ap.all_table_names, ap.current_audit_process_status, ap.current_audit_node_status, ap.out_table_name, ap.apply_type, ap.period_field, ap.period_type, ap.application_type, ap.application_no, ap.doc_id, ap.doc_name, ap.apply_comment, ap.doc_authorization_json, ap.permission_json, ap.apply_reason, ap.old_application_id, ap.new_application_id, ap.pm, ap.deliver_time, ap.deliver_time_type, ap.project_id, ap.apply_remark, ap.expect_time, ap.assets_id, ap.new_asset_id, ap.assets_create_time, ap.config_type, ap.config_sql, ap.config_sql_workflow_id, ap.workflow_id, ai.name as docName, ap.data_state, ap.data_amount, ap.data_cost, ap.assets_attach_json, json_extract(ap.granularity_json,"$[2].selectGranularity") as push_project_name, ap.relate_dict FROM tg_application_info ap LEFT JOIN tg_project pro on ap.project_id = pro.id
LEFT JOIN t_customer tc on pro.customer_id = tc.id
-- LEFT JOIN tg_template_info te ON ap.template_id = te.id -- LEFT JOIN tg_audit_process_info au ON ap.process_id = au.id LEFT JOIN tg_doc_info ai on ap.doc_id = ai.id
LEFT JOIN table_info ti on ap.base_table_id = ti.id
1=1
AND ap.data_state = #{search.dataState}
AND FIND_IN_SET(#{userId},ap.all_handlers)
and ap.copy = 0
AND ap.application_type IN
#{item}
and (ap.application_type = #{search.applicationType})
AND ap.application_no = #{search.applicationNo}
AND ai.id is not null
AND ap.process_id = #{search.auditId}
AND ap.applicant_name like concat('%',#{search.applyUser},'%')
and ap.require_time_type = #{search.searchRequireTimeType}
and tc.short_name like concat('%',#{search.searchClient},'%')
and ap.require_attr = #{search.searchReqiureAttr}
and ap.project_name like concat('%', #{search.projectName} ,'%')
and ap.current_audit_process_status = #{search.searchProcessStatus}
and (ap.project_name like concat('%',#{search.searchContent},'%') or ap.base_table_name like concat('%',#{search.searchContent},'%') or ap.applicant_name like concat('%',#{search.searchContent},'%') or ai.name like concat('%',#{search.searchContent},'%')) or ti.table_alias like concat('%',#{search.searchContent},'%')
order by ap.create_time asc
order by ap.create_time desc
